import React, { Component } from 'react';
import { Map, TileLayer } from 'react-leaflet';

export default class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      cities: props.cities,
      lat: 51.505,
      long: -0.09
    }

    this.search = this.search.bind(this);
  }

  search(cityName) {
    this.setState((state) => {
      console.log(state.cities);
      console.log(cityName);
      let currentCity = {}; // state.cities.filter(['Kabul', cityName]);

      state.cities.forEach((city) => {
        if(city.name === cityName) {
          currentCity = city;
        }
      });

      // cities.filter((city) => city.name.toLowerCase() == cityName.toLowerCase());
      // let currentCity = state.cities[3];
      console.log(currentCity);
      state.lat = currentCity.lat;
      state.long = currentCity.long;
      return state;
    })
  }

  render() {
    return (
      <body>
        <h2>Safety</h2>
        <CityForm search={this.search} />
        <CityMap lat={this.state.lat} lng={this.state.long} />
        <div className="row">
          <div className="col">
            <li>Emergency Phone Number: <em>9-1-1</em></li>
            <li>Nearest Emergency Room: <em>Virginia Mason </em></li>
            <li>Non-Emergency Police Phone Number: <em>206-625-5011</em></li>
          </div>
          <div className="col">
            <img src="img/sea-map.png" alt="Seattle" id="solid" />
          </div>
        </div>
        <div>
          This is an example of a few bits of emergency information that would be provided for a
          user that has selected Seattle as their location. Other examples of resources could be
          pest control, poison control, or fire department.
                </div>
        <footer>Project by Lauren Smith</footer>
      </body>
    )
  }
}

class CityMap extends Component {
  render() {
    const position = [this.props.lat, this.props.lng]
    return (
      <Map center={position} zoom={13}>
        <TileLayer
          attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
      </Map>
    )
  }
}


class CityForm extends Component {

  constructor(props) {
    super(props);
    this.state = { city: '' };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({ city: event.target.value });
  }

  handleSubmit(event) {
    //console.log('handleSubmit');
    event.preventDefault();
    this.props.search(this.state.city);
  }

  // render() {
  //     return (
  //         <div className='form-inline'>
  //             Search for a major city...
  //         <div className="mr-3">
  //                 <input type="text" name="term" id="searchQuery" className="form-control" />
  //             </div>
  //             <button type="submit" className="btn btn-primary">
  //                 Search!
  //         </button>
  //         </div>
  //     )
  // }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Search for a major city...
            <input type="text" value={this.state.city} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
    );
  }
}

